#include <stdio.h>
#include <string.h>
#include "utils.h"

#define LENGTH 1000

long solution(){
    char number[LENGTH] = "";

  int i, y, x, value;
  long result, max_result;

  max_result = 0L;
  y = 13;

  result = number[0] - '0'; // converts char to int/long;

  for(i = 1; i < LENGTH; i++){

    for(x = i; x < y; x++){
      value = number[x] - '0';
      
      if(value == 0){
        break;
      }

      result = result * (number[x] - '0');
    }

    if(result > max_result){
      max_result = result;
    }

    result = number[i] - '0';
    y++;
  }

  return max_result;
}

int main(int argc, char *argv[]){
    return long_time_it(solution);
}
  